import React from 'react'

import {
  CollectionPreviewContainer,
  TitleContainer,
  PreviewContainer
} from './collection-preview.styles'

const CollectionPreview = ({ title, items }) => (
  <CollectionPreviewContainer>
    <TitleContainer>
      {title.toUpperCase()}
    </TitleContainer>
    <PreviewContainer>
      {items
        .filter((item, idx) => idx < 4)
        .map(item => (
          <div key={item.id} item={item}>
            {item.name}
          </div>
        ))}
    </PreviewContainer>
  </CollectionPreviewContainer>
);

export default CollectionPreview
